thisfile="${BASH_SOURCE[0]}"
thisdir="$( cd "$( dirname "${thisfile}" )" && pwd )"
parentdir="$(dirname "$thisdir")"
dockerdir="$parentdir"
version="$(cat "$dockerdir"/version)"

TAG=$version
PARENT="python:3.5.1-slim"
NAME=$(basename "$thisdir"):"$TAG"

echo
echo ==============================================
echo creating Dockerfile for $NAME...
cat > "$thisdir"/Dockerfile <<EOF
#==========================================================
# Generated by 'build.sh'.
#
# DO NOT EDIT.
#==========================================================

FROM ${PARENT}
EOF

cat >> "$thisdir"/Dockerfile <<'EOF'

USER root


#------------------
# group and user

ENV GROUP=docker-users
ENV USER=docker-user
ENV HOME=/home/${USER}

RUN groupadd --gid 1000 ${GROUP} \
    && mkdir -p ${HOME} \
    && useradd --uid 1000 --gid ${GROUP} --no-user-group --home ${HOME} --shell /bin/bash ${USER} \
    && chown -R ${USER}:${GROUP} ${HOME}


#----------------------
# System and basic

# 'curl': about 15 MB.
# 'locales': about 16MB.

ENV SHELL=/bin/bash

ADD https://github.com/zpz/linux/blob/master/bash/bashrc /etc/bash.bashrc
# curl -sL https://github.com/zpz/linux/archive/master.tar.gz -o - |tar xz

RUN apt-get update \
    && apt-get install -y --no-install-recommends \
        apt-utils \
        curl \
        less \
        locales \
    \
    && chmod +r /etc/bash.bashrc \
    \
    && rm -rf /var/lib/apt/lists/* /tmp/* \
    && apt-get -y autoremove \
    && apt-get clean

# Locale
#   default is C.UTF-8

ENV LANG en_US.UTF-8
ENV LANGUAGE en_US:en
ENV LC_ALL en_US.UTF-8

RUN echo 'en_US.UTF-8 UTF-8' >> /etc/locale.gen \
    && locale-gen en_US.UTF-8 \
    && /usr/sbin/update-locale LANG=en_US.UTF-8


#----------------------
# Python

# Note: offical Python image gets the link 'python-config' wrong.

RUN pip install --no-cache-dir --upgrade \
        'pip==8.1.1' \
        'setuptools==20.10.1' \
        'wheel==0.29.0' \
    && pip install --no-cache-dir --upgrade \
        'pytest==2.9.1' \
        'requests==2.10.0' \
        'sh==1.11' \
        'toolz==0.7.4' \
    && ln -s -f /usr/local/bin/python3-config /usr/local/bin/python-config \
    && ln -s /usr/local/bin/py.test /usr/local/bin/pytest


#------------------------
# entrypoint and startup

ENV TINI_VERSION v0.9.0
ADD https://github.com/krallin/tini/releases/download/${TINI_VERSION}/tini /usr/bin/tini
RUN chmod +x /usr/bin/tini

ENTRYPOINT ["/usr/bin/tini", "--"]
# TODO: problematic when I terminate some programs within container.

USER ${USER}
WORKDIR ${HOME}
EOF


echo
echo building image "$NAME"...
echo
docker build -t "$NAME" "$thisdir"

